using FluentAssertions;
using Microsoft.Extensions.Logging.Abstractions;
using Mintsafe.Abstractions;
using System;
using System.Collections.Generic;
using System.IO;
using System.Linq;
using System.Text.Json;
using Xunit;
using static Mintsafe.Lib.UnitTests.FakeGenerator;

namespace Mintsafe.Lib.UnitTests;

public class MetadataJsonBuilderShould
{
    private MetadataJsonBuilder _metadataJsonBuilder;

    private static readonly JsonSerializerOptions SerialiserOptions = new()
    {
        PropertyNamingPolicy = JsonNamingPolicy.CamelCase,
    };

    public MetadataJsonBuilderShould()
    {
        _metadataJsonBuilder = new MetadataJsonBuilder(
            NullLogger<MetadataJsonBuilder>.Instance,
            NullInstrumentor.Instance,
            GenerateSettings());
    }

    [Theory]
    [InlineData(1)]
    [InlineData(2)]
    [InlineData(15)]
    public void Generate_The_Right_Json_With_Correct_Token_Metadata(int nftCount)
    {
        var collection = GenerateCollection();
        var tokens = GenerateTokens(nftCount).ToArray();

        var json = _metadataJsonBuilder.GenerateNftStandardJson(
            tokens, collection);

        var deserialised = JsonSerializer.Deserialize<Dictionary<
            string, // 721
            Dictionary<
                string, // PolicyID
                Dictionary<
                    string, // AssetName
                    CnftStandardAsset>>>>(json, SerialiserOptions);

        deserialised.Should().NotBeNull();
#pragma warning disable CS8602 // Dereference of a possibly null reference.
        var policyAssets = deserialised["721"][collection.PolicyId];
#pragma warning restore CS8602 // Dereference of a possibly null reference.

        policyAssets.Keys.Count.Should().Be(nftCount);
        foreach (var token in tokens)
        {
            var asset = policyAssets[token.AssetName];
            asset.Should().NotBeNull();
            asset.Name.Should().Be(token.Name);
            asset.Description.Should().Be(token.Description);
            asset.Creators.Should().BeEquivalentTo(token.Creators);
            asset.Publishers.Should().BeEquivalentTo(collection.Publishers);
            asset.Image.Should().Be(token.Image);
            asset.MediaType.Should().Be(token.MediaType);
            foreach (var file in token.Files)
            {
                if (asset.Files == null)
                    throw new Exception("Files cannot be null");
                var assetFile = asset.Files.First(f => f.Name == file.Name);
                assetFile.Name.Should().Be(file.Name);
                assetFile.Src.Should().Be(file.Url);
                assetFile.MediaType.Should().Be(file.MediaType);
                assetFile.Hash.Should().Be(file.FileHash);
            }
        }
    }

    [Theory]
    [InlineData(1, "data:image/svg+xml;base64,PHN2ZyB2ZXJzaW9uPSIxLjAiIHhtbG5zPSJodHRwOi8vd3d3LnczLm9yZy8yMDAwL3N2ZyIgd2lkdGg9IjQyNi4wMDAwMDBwdCIgaG", null)]
    [InlineData(2, null, "data:image/svg+xml;base64,PHN2ZyB2ZXJzaW9uPSIxLjAiIHhtbG5zPSJodHRwOi8vd3d3LnczLm9yZy8yMDAwL3N2ZyIgd2lkdGg9IjQyNi4wMDAwMDBwdCIgaG")]
    [InlineData(4, "", "data:image/svg+xml;base64,PHN2ZyB2ZXJzaW9uPSIxLjAiIHhtbG5zPSJodHRwOi8vd3d3LnczLm9yZy8yMDAwL3N2ZyIgd2lkdGg9IjQyNi4wMDAwMDBwdCIgaG")]
    [InlineData(15, "data:image/svg+xml;base64,PHN2ZyB2ZXJzaW9uPSIxLjAiIHhtbG5zPSJodHRwOi8vd3d3LnczLm9yZy8yMDAwL3N2ZyIgd2lkdGg9IjQyNi4wMDAwMDBwdCIgaG", "data:image/svg+xml;base64,PHN2ZyB2ZXJzaW9uPSIxLjAiIHhtbG5zPSJodHRwOi8vd3d3LnczLm9yZy8yMDAwL3N2ZyIgd2lkdGg9IjQyNi4wMDAwMDBwdCIgaG")]
    public void Generate_The_Right_Json_With_Correct_Token_OnChain_Metadata(int nftCount, string onChainImage, string onChainFile)
    {
        var collection = GenerateCollection();
        var tokens = GenerateOnChainTokens(nftCount, onChainImage, onChainFile).ToArray();

        var json = _metadataJsonBuilder.GenerateNftStandardJson(
            tokens, collection);

        var deserialised = JsonSerializer.Deserialize<Dictionary<
            string, // 721
            Dictionary<
                string, // PolicyID
                Dictionary<
                    string, // AssetName
                    CnftOnChainStandardAsset>>>>(json, SerialiserOptions);

        deserialised.Should().NotBeNull();
#pragma warning disable CS8602 // Dereference of a possibly null reference.
        var policyAssets = deserialised["721"][collection.PolicyId];
#pragma warning restore CS8602 // Dereference of a possibly null reference.

        policyAssets.Keys.Count.Should().Be(nftCount);
        foreach (var token in tokens)
        {
            var asset = policyAssets[token.AssetName];
            asset.Should().NotBeNull();
            asset.Name.Should().Be(token.Name);
            asset.Description.Should().Be(token.Description);
            asset.Creators.Should().BeEquivalentTo(token.Creators);
            asset.Publishers.Should().BeEquivalentTo(collection.Publishers);
            if (!string.IsNullOrEmpty(token.Image))
            {
                asset.Image.Should().BeEquivalentTo(MetadataJsonBuilder.SplitStringToChunks(token.Image));
            }
            asset.MediaType.Should().Be(token.MediaType);
            foreach (var file in token.Files)
            {
                if (asset.Files == null)
                    throw new Exception("Files cannot be null");
                var assetFile = asset.Files.First(f => f.Name == file.Name);
                assetFile.Name.Should().Be(file.Name);
                assetFile.Src.Should().BeEquivalentTo(MetadataJsonBuilder.SplitStringToChunks(file.Url));
                assetFile.MediaType.Should().Be(file.MediaType);
                assetFile.Hash.Should().Be(file.FileHash);
            }
        }
    }

