    [InlineData("data:image/svg+xml;base64,PHN2ZyB2ZXJzaW9uPSIxLjAiIHhtbG5zPSJodHRwOi8vd3d3LnczLm9yZy8yMDAwL3N2ZyIgd2lkdGg9IjQyNi4wMDAwMDBwdCIgaGVpZ2h0PSI0MDQuMDAwMDAwcHQiIHZpZXdCb3g9IjAgMCA0MjYuMDAwMDAwIDQwNC4wMDAwMDAiIHN0eWxlPSJiYWNrZ3JvdW5kLWNvbG9yOiNmZmYiIHByZXNlcnZlQXNwZWN0UmF0aW89InhNaWRZTWlkIG1lZXQiPg0KDQogICAgPGcgdHJhbnNmb3JtPSJ", 6)]
    public void Split_Large_Strings(string value, int expectedItems)
    {
        var items = MetadataJsonBuilder.SplitStringToChunks(value);
        var itemsTotalLength = items.Sum(s => s.Length);
        items.Should().HaveCount(expectedItems);
        itemsTotalLength.Should().Be(value.Length);
    }

